bool lost_assert(S,G,I) {
  if ( RPF_interface(S) == I ) {
     return FALSE
  } else {
     return ( AssertWinner(S,G,I) != NULL AND
              AssertWinner(S,G,I) != me  AND
              (AssertWinnerMetric(S,G,I) is better
                 than spt_assert_metric(S,I) )
  }
}

