orig_text:
grouping vxlan-header {
  description
    "The VXLAN encapsulation header information.";
  choice vxlan-type {
    description
      "NVGRE can use either an IPv4
       or an IPv6 header for encapsulation.";
    case ipv4 {
      uses ipv4-header;
    }
    case ipv6 {
      uses ipv6-header;
    }
  }
  leaf vxlan-identifier {
    type uint32;
    mandatory true;
    description
      "The VXLAN identifier of the VXLAN header.";
  }
}

