orig_text:
rpc route-delete {
   description
     "To delete a route or a list of routes from a RIB";
   input {
     leaf return-failure-detail {
       type boolean;
       default "false";
       description
         "Whether to return the failure detail.
          true  - return the failure detail
          false - do not return the failure detail
          The default is false.";
     }
     leaf rib-name {
       type string;
       mandatory true;
       description
         "A reference to the name of a RIB.";
     }
     container routes {
       description
         "The routes to be added to the RIB.";
       list route-list {
         key "route-index";
         description
           "The list of routes to be deleted.";
         uses route-prefix;
       }
     }
   }
   output {
     uses route-operation-state;
   }
 }

correct_text:
rpc route-delete {
   description
     "To delete a route or a list of routes from a RIB";
   input {
     leaf return-failure-detail {
       type boolean;
       default "false";
       description
         "Whether to return the failure detail.
          true  - return the failure detail
          false - do not return the failure detail
          The default is false.";
     }
     leaf rib-name {
       type string;
       mandatory true;
       description
         "A reference to the name of a RIB.";
     }
     container routes {
       description
         "The routes to be deleted from the RIB.";
       list route-list {
         key "route-index";
         description
           "The list of routes to be deleted.";
         uses route-prefix;
       }
     }
   }
   output {
     uses route-operation-state;
   }
 }

