orig_text:
container vxlan {
  when "derived-from-or-self(../type, "
     + "'l2vpn-svc:vxlan')" {
    description
      "Only applies when the type of the tagged
       interface is 'vxlan'.";
  }
  if-feature "vxlan";
  leaf vni-id {
    type uint32;
    mandatory true;
    description
      "VXLAN Network Identifier (VNI).";
  }
  leaf peer-mode {
    type identityref {
      base vxlan-peer-mode;
    }
    default "static-mode";
    description
      "Specifies the VXLAN access mode.  By default,
       the peer mode is set to 'static-mode'.";
  }
  list peer-list {
    key "peer-ip";
    leaf peer-ip {
      type inet:ip-address;
      description
        "Peer IP.";
    }
    description
      "List of peer IP addresses.";
  }
  description
    "QinQ.";
}

